      name: "Product 2",
      description: "Description 2",
      price: "$29.99",
    },
    {
      image: Pimage,
      star: Rstar,
      name: "Product 2",
      description: "Description 2",
      price: "$29.99",
    },
    {
      image: Pimage,
      star: Rstar,
      name: "Product 2",
      description: "Description 2",
      price: "$29.99",
    },
    {
      image: Pimage,
      star: Rstar,
      name: "Product 2",
      description: "Description 2",
      price: "$29.99",
    },
    {
      image: Pimage,
      star: Rstar,
      name: "Product 2",
      description: "Description 2",
      price: "$29.99",
    },
    // Add more card data as needed
  ];

  const [slideIndex, setSlideIndex] = useState(0);

  const handleSlide = (direction) => {
    const newIndex =
      direction === "left"
        ? (slideIndex - 1 + cardData.length) % cardData.length
        : (slideIndex + 1) % cardData.length;

    setSlideIndex(newIndex);
  };

  return (
    <div className="slideshow-container">
      <div className="dynamic-content" style={{ transform: `translateX(-${slideIndex * 100}%)` }}>
        {cardData.map((card, index) => (
          <Card
            key={card.name} // Assuming 'name' is unique for each card
            image={card.image}
            star={card.star}
            name={card.name}
            description={card.description}
            price={card.price}
          />
        ))}
      </div>

      <button className="prev" onClick={() => handleSlide("left")}>
        &#10094;
      </button>
      <button className="next" onClick={() => handleSlide("right")}>
        &#10095;
      </button>
    </div>
  );
}
